INSERT INTO competency_band (bandLevelId, competencyId, subheading, information) VALUES
	('1', '1', 'Developing self-awareness', 'Understands own strengths and areas of development. Self-aware of own wellbeing and seeks support where appropriate.'),
	('1', '1', 'Managing yourself', 'Works with People Manager to sets and achieve goals by monitoring progress regularly against performance.'),
	('1', '1', 'Continuing personal development', 'Flexible and willingness to learnon the job while proactively keeping up to date with the knowledge and skills needed.'),
	('1', '1', 'Acting with integrity', 'Understands the company values and applies this to own principles. Is open and honest and acts respectfully with others and customers.'),
	('2', '1', 'Developing self-awareness', 'Understands others strengths and areas for development. Recognising diversity and its value within self andteam. Proactively uses wellbeing tools to support self-regulation.'),
	('2', '1', 'Managing yourself', 'Able to identify own goals and discusses these with People Manager. Understands the need to work conscientiously to achieve tasks on schedule.'),
	('2', '1', 'Continuing personal development', 'Identifies learning gaps and seeks out opportunities to improve skills. Open to developmental feedback fromothers.'),
	('2', '1', 'Acting with integrity', 'Demonstrates positive behaviours when dealing with others and ensures application of the values while working and representing Kainos to our customers.'),
	('3', '1', 'Developing self-awareness', 'Seeks out new challenges and opportunitiesthat may stretchcurrentabilities.Builds on own self-awareness of overall wellbeing.'),
	('3', '1', 'Managing yourself', 'Recognises the need for SMART goals, and demonstrates a “can do” attitude, through having own internal standards of performance.'),
	('3', '1', 'Continuing personal development', 'Seeks out opportunities to improve skills beyond the role scope whilst also seekingregular feedback.'),
	('3', '1', 'Acting with integrity', 'Encourages positive behaviours in others, while role modelling the Kainos values.'),
	('4', '1', 'Developing self-awareness', 'Understands and confidently articulates own learning and developmental needs and proactively seek opportunities to gain experience.Has a strong level of self-awareness and in tune with own wellbeing needs and intuitive of others.'),
	('4', '1', 'Managing yourself', 'Seeks and responds positively to feedback regarding own learning and development. Approach with a willingness to take on tasks that are outside role scope.'),
	('4', '1', 'Continuing personal development', 'Looks for opportunities to take on new challenges while proactively supporting and encouraging others in identifying learning needs.'),
	('4', '1', 'Acting with integrity', 'Recognises inappropriate behaviours and challenges constructively while promoting the Kainos values. Adapting behaviours and acting in the most appropriate way to enable others to respond constructively.'),
	('5', '1', 'Developing self-awareness', 'Understands own personal preferences, biases and values different cultures, backgrounds and circumstances in decision making and takes actions.Champions Kainos wellbeing offerings and provides direction to the various wellbeing guides and support availablefor our people.'),
	('5', '1', 'Managing yourself', 'Consistently sets own goals and manages this independently. Making autonomous decisions and are able to ‘get on with the job’ escalating decisions only when appropriate.'),
	('5', '1', 'Continuing personal development', 'Identifies and addresses team or individual capability requirements and gaps to deliver current and future work. Consistently identifies and develops self and others to support talent development.'),
	('5', '1', 'Acting with integrity', 'Demonstrates professional and organisational values through actions and behaviours. Behaves in an inclusive manner and respects equality and diversity.'),
	('6', '1', 'Developing self-awareness', 'Reflects on how factors such as own values, prejudices and emotions influences own judgement, behaviour, and self-belief. Uses feedback from appraisals and other sources to consider personal impact and changes behaviour. Understands personal sources of stress and wellbeing.'),
	('6', '1', 'Managing yourself', 'Plans and manages own time effectively and fulfils work requirements andcommitments to a high standard, without compromising own health and wellbeing. Remains calm andfocused under pressure. '),
